const Xfetch = require("./Xfetch");

class Xgmail {
    constructor() {
    }
    static live(email) {
        let fetch = new Xfetch();
        return fetch.get(`https://calendar.google.com/calendar/ical/${email}/public/basic.ics`)
            .then(_ => fetch.headers['x-frame-options'] ? true : false)
    }

}

module.exports = Xgmail;
